package sort.bubble;import java.util.Arrays;public class Main {    public static void main(String[] args) {        int[] array = {9, 8, 7, 6, 5, 4, 3, 2, 1};        bubbleSort(array);        System.out.println(Arrays.toString(array));    }    static void bubbleSort(int[] sortArray) {        for (int lastIndex = sortArray.length - 1; lastIndex > 0; lastIndex--) {            for (int index = 0; index < lastIndex && sortArray[index] > sortArray[index + 1]; index++) {                swap(sortArray, index, index + 1);            }        }    }    static void swap(int[] array, int switchA, int switchB) {        if (switchA == switchB) {            return;        }        var temp = array[switchA];        array[switchA] = array[switchB];        array[switchB] = temp;    }}